function showFeedbackForm(){document.querySelector(".map-contacts-btn").addEventListener("click",function(e){e.preventDefault(),document.querySelector(".feedback-form").style.display="-ms-flexbox",document.querySelector(".feedback-form").style.display="flex"}),closeFeedbackForm()}function closeFeedbackForm(){document.querySelector(".feedback-form-close").addEventListener("click",function(e){e.preventDefault(),document.querySelector(".feedback-form").style.display="none"})}function hidePlaceholders(){for(var e=document.querySelectorAll(".input-cover input"),t=0;t<e.length;t++)e[t].addEventListener("focus",function(){hidePlaceholder(this)});document.querySelector(".text-area-cover textarea").addEventListener("focus",function(){hidePlaceholder(this)})}function hidePlaceholder(e){var t=e.getAttribute("placeholder");e.removeAttribute("placeholder"),e.addEventListener("blur",function(){this.setAttribute("placeholder",t)})}document.addEventListener("DOMContentLoaded",function(){showFeedbackForm(),hidePlaceholders()});